import "./index.css";
import React from "react";

function Logo() {
  return (
    <div>
      <h1>🌴Far Away👜</h1>
    </div>
  );
}

function Form({ setItems }) {
  function handleSubmit(event) {
    event.preventDefault();
    if (description === "") {
      return;
    } else {
      setItems((items) => [...items, { description, quantity, packed: false }]);
      setDescription("");
      setQuantity(1);
    }
  }

  const [description, setDescription] = React.useState("");
  const [quantity, setQuantity] = React.useState(1);

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your Trip?</h3>

      <select
        value={quantity}
        onChange={(event) => setQuantity(Number(event.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((optionNum) => (
          <option key={optionNum} value={optionNum}>
            {optionNum}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      ></input>
      <button>ADD</button>
    </form>
  );
}
function handleDelete(item, setItems) {
  setItems((items) => items.filter((items) => items.description !== item));
}

function Item({ item, setItems, handleCheckBox }) {
  return (
    <div className="list">
      <li>
        <input
          type="checkbox"
          value={item.packed}
          onChange={() => handleCheckBox(item.description, setItems)}
        ></input>
        <span style={item.packed ? { textDecoration: "line-through" } : {}}>
          {item.quantity} - {item.description}
        </span>
        <button onClick={() => handleDelete(item.description, setItems)}>
          ❌
        </button>
      </li>
    </div>
  );
}

function PackingList({ items, setItems, handleCheckBox, handleReset }) {
  let [currentSort, setCurrentSort] = React.useState("input");
  let sortItems = items;

  function handleChange(e) {
    setCurrentSort(e.target.value);
    console.log(currentSort);
  }
  if (currentSort === "input") {
    sortItems = items;
  }
  if (currentSort === "description") {
    sortItems = items
      .slice()
      .sort((a, b) => a.description.localeCompare(b.description));
  }
  if (currentSort === "packed") {
    sortItems = items
      .slice()
      .sort((a, b) => Number(a.packed) - Number(b.packed));
  }

  return (
    <div className="list">
      <ul>
        {sortItems.map((items) => (
          <Item
            item={items}
            setItems={setItems}
            handleCheckBox={handleCheckBox}
          />
        ))}
      </ul>
      <div className="actions">
        <select value={currentSort} onChange={handleChange}>
          <option value="input">Sort By Input</option>
          <option value="description">Sort By Description</option>
          <option value="packed">Sort By Packed</option>
        </select>

        <button onClick={handleReset}>RESET</button>
      </div>
    </div>
  );
}

function Stats({ items }) {
  const length = items.length;
  const packed = items.filter((item) => item.packed).length;
  const percentage = Math.round((packed / length) * 100);
  return (
    <footer className="stats">
      <em>
        {length === 0
          ? "Start Adding Items"
          : percentage === 100
          ? "You are all set to go. Enjoy!!😉"
          : ` You have ${length} list of items on your list, and you already packed
        ${packed} (${percentage} %) items`}
      </em>
    </footer>
  );
}

export default function App() {
  const [items, setItems] = React.useState([]);
  function handleCheckBox(description) {
    setItems((items) =>
      items.map((item) =>
        item.description === description
          ? { ...item, packed: !item.packed }
          : { ...item }
      )
    );
  }

  function handleReset() {
    setItems({});
  }

  return (
    <div className="app">
      <Logo />
      <Form setItems={setItems} />
      <PackingList
        items={items}
        setItems={setItems}
        handleCheckBox={handleCheckBox}
      />
      <Stats items={items} />
    </div>
  );
}
