import "./index.css";
import React from "react";

function Logo() {
  return (
    <div>
      <h1>üå¥Far Awayüëú</h1>
    </div>
  );
}

function Form({ setItems }) {
  function handleSubmit(event) {
    event.preventDefault();
    if (description === "") {
      return;
    } else {
      setItems((items) => [...items, { description, quantity, packed: false }]);
      setDescription("");
      setQuantity(1);
    }
  }

  const [description, setDescription] = React.useState("");
  const [quantity, setQuantity] = React.useState(1);

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your Trip?</h3>

      <select
        value={quantity}
        onChange={(event) => setQuantity(Number(event.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((optionNum) => (
          <option key={optionNum} value={optionNum}>
            {optionNum}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      ></input>
      <button>ADD</button>
    </form>
  );
}
function handleDelete(item, setItems) {
  setItems((items) => items.filter((items) => items.description !== item));
}

function Item({ item, setItems, handleCheckBox }) {
  return (
    <li>
      <input
        type="checkbox"
        value={item.packed}
        onChange={() => handleCheckBox(item.description, setItems)}
      ></input>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} - {item.description}
      </span>
      <button onClick={() => handleDelete(item.description, setItems)}>
        ‚ùå
      </button>
    </li>
  );
}

function PackingList({ items, setItems, handleCheckBox }) {
  return (
    <div className="list">
      <ul>
        {items.map((items) => (
          <Item
            item={items}
            setItems={setItems}
            handleCheckBox={handleCheckBox}
          />
        ))}
      </ul>
    </div>
  );
}

function Stats({ items }) {
  const length = items.length;
  const packed = items.filter((item) => item.packed).length;
  const percentage = (packed / length) * 100;
  return (
    <footer className="stats">
      <em>
        You have {length} list of items on your list, and you already packed{" "}
        {packed}({percentage}) items
      </em>
    </footer>
  );
}

export default function App() {
  const [items, setItems] = React.useState([]);
  function handleCheckBox(description) {
    setItems((items) =>
      items.map((item) =>
        item.description === description
          ? { ...item, packed: !item.packed }
          : { ...item }
      )
    );
  }

  return (
    <div className="app">
      <Logo />
      <Form setItems={setItems} />
      <PackingList
        items={items}
        setItems={setItems}
        handleCheckBox={handleCheckBox}
      />
      <Stats items={items} />
    </div>
  );
}
