import "./index.css";
import React from "react";

function Logo() {
  return (
    <div>
      <h1>üå¥Far Awayüëú</h1>
    </div>
  );
}

function Form({ setItems, packed }) {
  function handleSubmit(event) {
    event.preventDefault();
    setItems((items) => [...items, { description, quantity, packed }]);
    setDescription("");
    setQuantity(1);
  }

  const [description, setDescription] = React.useState("");
  const [quantity, setQuantity] = React.useState(1);

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your Trip?</h3>

      <select
        value={quantity}
        onChange={(event) => setQuantity(Number(event.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((optionNum) => (
          <option key={optionNum} value={optionNum}>
            {optionNum}
          </option>
        ))}
      </select>

      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      ></input>
      <button>ADD</button>
    </form>
  );
}

function Item({ item, packed, setPacked }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} - {item.description}
      </span>
      <button onClick={() => (item.packed = true)}>‚ùå</button>
    </li>
  );
}

function PackingList({ items, setPacked, packed }) {
  return (
    <div className="list">
      <ul>
        {items.map((items) => (
          <Item
            item={items}
            key={items.id}
            setPacked={setPacked}
            packed={packed}
          />
        ))}
      </ul>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>
        You have X list of itemson your list, anad you already packed X (%X)
      </em>
    </footer>
  );
}

export default function App() {
  const [items, setItems] = React.useState([]);
  const [packed, setPacked] = React.useState(false);

  return (
    <div className="app ">
      <Logo />
      <Form setItems={setItems} packed={packed} />
      <PackingList items={items} setPacked={setPacked} packed={packed} />
      <Stats />
    </div>
  );
}
