import React from "react";
import "./App.css";

const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];

function Header() {
  return (
    <div>
      <header className="header">
        <h1>Fast React Pizza Co.</h1>
      </header>
    </div>
  );
}

function Pizza({ pizza }) {
  //destructuring props {props1,props2}
  return (
    <div className={`pizza ${pizza.soldOut ? " sold-out" : ""} `}>
      <img src={pizza.photoName} alt="Spinaci pizza" />
      <div>
        <h3>{pizza.name}</h3>
        <p>{pizza.ingredients}</p>
        <span>{pizza.soldOut ? "SOLD OUT " : "$" + pizza.price}</span>
      </div>
    </div>
  );
}

function Menu() {
  return (
    <div className="menu">
      <h2>Our Menu</h2>
      <div className="pizzas">
        {pizzaData.map((pizzas) => (
          <Pizza pizza={pizzas} />
        ))}
      </div>
    </div>
  );
}

const isOpen = new Date().getHours() >= 8 && new Date().getHours() <= 20;
function Footer() {
  return (
    <div>
      <footer className="footer">
        {isOpen ? (
          <div className="order">
            <p>We are Open until 20:00. Come Visit Us</p>
            <button className="btn">Order Now</button>
          </div>
        ) : (
          <p className="order">We are currently closed :)</p>
        )}
      </footer>
    </div>
  );
}

export default function App() {
  return (
    <div className="container">
      <Header />
      <Menu />
      <Footer />
    </div>
  );
}
