import React from "react"
import "./navbar_banner.css"
import Display from "./navbar_banner_data"
import left from "./Images/left.png"
import right from "./Images/right.png"
import { Link } from "react-router-dom"

export default function Banner(){

const data = Display.map(data=>{

   var length = Display.length
   console.log(length)

    return(
        <BannerData
        name= {data.name}
        img= {data.img}
        description = {data.description}
         />
    )
})


 let i=0

 const [productDisplay,setProductDisplay] = React.useState(data[i],)

//On Click Function
 function Left(){
        if(i==0){
            return(
                setProductDisplay(data[i])
            )
        }
        else{
            setProductDisplay(data[i=i-1])
        }
 }
 function Right(){
    if(i==length-1){
        return(
            setProductDisplay(data[i])
        )
    }
    else{
        setProductDisplay(data[i=i+1])
    }
 }


function BannerData(props){
    if(i==0){
        return(
            <div id="banner">
            <div className="end"></div>

               <div id="banner-text">
               <Link to="/products" ><a>{props.name}</a></Link>
                <p>{props.description}</p>
                </div> 

                <div id="banner-img">
                    <img src={props.img} />
                </div>

                <div className="slider" onClick={Right}><img src={right} /></div>   

            </div>
        )

    }
    else if(i==4){
        return(
            <div id="banner">

                <div className="slider" onClick={Left}><img src={left} /></div>

               <div id="banner-text">
               <Link to="/products" ><a>{props.name}</a></Link>
                <p>{props.description}</p>
                </div> 

                <div id="banner-img">
                    <img src={props.img} />
                </div> 

              <div className="end"></div>     
            </div>
        )
    }
    else{
        return(
            <div id="banner">

                <div className="slider" onClick={Left}><img src={left} /></div>

               <div id="banner-text">
               <Link to="/products"><a>{props.name}</a></Link>
                <p>{props.description}</p>
                </div> 

                <div id="banner-img">
                    <img src={props.img} />
                </div>

                <div className="slider" onClick={Right}><img src={right} /></div>

            </div>
        )
    }

}

   return(
    <div>
         {productDisplay}
    </div>
   )
}
