import { eventWrapper } from "@testing-library/user-event/dist/utils";
import "./index.css";
import React from "react";

function Logo() {
  return (
    <div>
      <h1>üå¥Far Awayüëú</h1>
    </div>
  );
}
function Form() {
  function handleSubmit(event) {
    event.preventDefault();
  }

  const [description, setDescription] = React.useState("");
  const [quantity, setQuantity] = React.useState(1);

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your Trip?</h3>
      <select
        value={quantity}
        onChange={(event) => setQuantity(event.quantity.value)}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((optionNum) => (
          <option key={optionNum} value={optionNum}>
            {optionNum}
          </option>
        ))}
      </select>
      {console.log(quantity)}
      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(event) => setDescription(event.target.value)}
      ></input>
      <button>ADD</button>
    </form>
  );
}

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: false },
  { id: 2, description: "Socks", quantity: 12, packed: false },
  { id: 3, description: "Torch", quantity: 1, packed: true },
];

function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} - {item.description}
        <button>‚ùå</button>
      </span>
    </li>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((items) => (
          <Item item={items} key={items.id} />
        ))}
      </ul>
    </div>
  );
}

function Stats() {
  return (
    <footer className="stats">
      <em>
        You have X list of itemson your list, anad you already packed X (%X)
      </em>
    </footer>
  );
}

export default function App() {
  return (
    <div className="app ">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}
